sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var query = request.queryParams.q || '';
    var limit = parseInt(request.queryParams.limit || 15);

    if (!query || query.trim().length < 2) {
        response.setStatus(400);
        response.setBody({ error: 'Query must be at least 2 characters.' });
        return;
    }

    try {
        var indexer  = new x_snc_demo_scope_0.TrustSearchIndexer();
        var reRanker = new x_snc_demo_scope_0.TrustReRanker();
        var tracker  = new x_snc_demo_scope_0.TrustBehaviorTracker();

        var raw = indexer.searchRaw(query, limit * 2);
        var ranked = reRanker.reRank(raw);
        var top = ranked.slice(0, limit);

        var sysIds = [];
        for (var i = 0; i < top.length; i++) {
            sysIds.push(top[i].sys_id);
        }
        tracker.recordImpression(query, sysIds);

        response.setStatus(200);
        response.setBody({
            query: query,
            total: top.length,
            results: top
        });
    } catch(e) {
        response.setStatus(500);
        response.setBody({ error: e.message });
    }

})(request, response);
